package leapyear;

public class Leapyear {

	public void findleapyear(int year) {
		if(year%4==0) {
			if(year%100==0) {
				if(year%400==0) {
					System.out.println("leap year");
				}
			}
			}else {
				System.out.println("Not leap year");
			}
			System.out.println("leap year");
		}
		}
